import React from "react";
import { useRef } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { Center } from "../components/styles";

const DeleteFlight = ({ flight }) => {
  const flightNumberRef = useRef();
  const navigate = useNavigate();

  const handleDelete = async (event) => {
    event.preventDefault();

    try {
      // use the flight number from the current clicked button to delete the flight
      await axios.delete(
        `http://localhost:8085/flights/${flight.flightNumber}`
      );

      // use to refresh just the component when deleted instead of the entire page
      // destructure out to pass the get all method into it in Home.jsx
      // That way each time the delete is pressed the parent re-renders as well
      flightInfo();
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <>
      <div style={{ margin: 30 }}>
        <Center>
          <form className="myForm" onSubmit={handleDelete}>
            <Center>
              <h1 style={{ color: "#FFAC42" }}>Delete Flight</h1>
            </Center>
            <div>
              <TextField
                type={"text"}
                inputRef={flightNumberRef}
                sx={{ width: 350, paddingBottom: 3 }}
                placeholder={" Flight Number"}
              ></TextField>
            </div>
            <Center>
              <Button
                type="submit"
                variant="contained"
                style={{
                  backgroundColor: "#FFAC42",
                  height: 50,
                  width: 150,
                  color: "white",
                  borderColor: "#ff7f50",
                }}
                onClick={handleDelete}
              >
                Delete Flight
              </Button>
            </Center>
          </form>
        </Center>
      </div>
    </>
  );
};

export default DeleteFlight;
