import React from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { useRef } from "react";

const DeleteFlight = ({ flight }) => {
  const flightNumberRef = useRef();
  const navigate = useNavigate();
  const handleDelete = async (event) => {
    try {
      await axios.delete(
        "http://localhost:8085/flights/" + flight.flightNumber
      );
      navigate(0);
    } catch (err) {
      console.error(err);
    }
  };
  return (
    <div>
      <div className="new">
        <div>
          <div>
            <div
              sx={{
                display: "flex",
                justifyContent: "center",
                alignItems: "center",

                "& > :not(style)": {
                  borderRadius: 10,
                  margin: 5,
                  paddingLeft: 10,
                  paddingRight: 10,
                  paddingBottom: 4,
                  paddingTop: 2,
                },
              }}
            >
              <div elevation={24} square={true}>
                <div>
                  <div className="myForm" onSubmit={handleDelete}>
                    <div style={{ display: "flex", flexDirection: "column" }}>
                      <div style={{ allignItem: "center" }}>
                        <div>
                          <h1>Delete Flight</h1>
                        </div>
                        <div>
                          <form
                            className="deleteFlightForm"
                            onSubmit={handleDelete}
                          >
                            <TextField
                              type={"text"}
                              inputRef={flightNumberRef}
                              style={{
                                height: 50,
                                width: 250,
                                borderWidth: "1px solid",
                              }}
                              placeholder={" Flight Number"}
                            ></TextField>
                          </form>
                        </div>

                        <div>
                          <Button
                            type="submit"
                            variant="contained"
                            style={{
                              backgroundColor: "#ff7f50",
                              marginTop: 30,
                              height: 50,
                              width: 150,
                              color: "white",
                              marginLeft: 30,
                              borderColor: "#ff7f50",
                            }}
                            onClick={handleDelete}
                          >
                            DELETE FLIGHT
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DeleteFlight;
